import React, { Component } from 'react';
import { Card, CardMedia, CardContent, Typography} from '@material-ui/core';
import '../../style.scss';

class CoverItems extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imageUrl: 'https://image.tmdb.org/t/p/w500'
        };
    }


    render() {

        const { imageUrl } = this.state

        const results = [];



        for (var i = 0; i < this.props.data.length; i++) {
            const movieOverlay = (this.props.overlay)?
            <CardContent className='CardOverlay'> 
                <i className="material-icons movieIcon ">movie</i>
                <div>
                    <Typography variant="body1" className='overlayText'>
                        {this.props.data[i].title}
                    </Typography>
                </div>
            </CardContent>
        :
        <div/>
            switch (this.props.data[i].media_type) {
                case 'movie':
                    results.push(<Card key={i} className='Card' onClick={() => {this.pageNavigate(this.props.data[i])} }>
                                    <div className='CardMediaWrapper'>
                                        <CardMedia
                                            className='CardMedia'
                                            image={imageUrl + this.props.data[i].backdrop_path}
                                            title={this.props.data[i].title}
                                        />
                                         {movieOverlay}
                                    </div>
                                </Card> 
                    );
                    break;
                case 'tv':
                    results.push(<Card key={i} className='Card' onClick={() => {this.pageNavigate(this.props.data[i])}} >
                                    <div className='CardMediaWrapper'>
                                        <CardMedia
                                            className='largeCardMedia'
                                            image={imageUrl + this.props.data[i].backdrop_path}
                                            title={this.props.data[i].name}
                                        />
                                        {/* if (this.props.overlay === true) {
                                                    <CardContent className='CardOverlay'> 
                                                    <i className="material-icons tvIcon ">tv</i>
                                                    <div>
                                                        <Typography variant="body1" className='overlayText'>
                                                            {this.props.data[i].name}
                                                        </Typography>
                                                    </div>
                                                    </CardContent>
                                                } */}
                                    </div>
                                </Card> 
                )
                    break;
                case 'person':
                    results.push(<Card key={i} className='Card' onClick={() => {this.pageNavigate(this.props.data[i])}} >
                                    <div className='CardMediaWrapper'>
                                        <CardMedia
                                            className='CardMedia'
                                            image={imageUrl + this.props.data[i].profile_path}
                                            title={this.props.data[i].name}
                                        />
                                        {/* if (this.props.overlay === true) {
                                                    <CardContent className='CardOverlay'> 
                                                    <i className="material-icons tvIcon ">tv</i>
                                                    <div>
                                                        <Typography variant="body1" className='overlayText'>
                                                            {this.props.data[i].name}
                                                        </Typography>
                                                    </div>
                                                    </CardContent>
                                                } */}
                                    </div>
                                </Card> 
                    )
                    break;
                default:
                    results.push('')
                    break;
            };
        }


        return (
<div>

    {results}

</div>

        );
    }
}


export default CoverItems
