import { IS_MOVIE_IMAGE_LOADED, IS_TV_IMAGE_LOADED, IS_PERSON_IMAGE_LOADED } from './types'


export const ImageLoading = ( mediaType, indexOf, id ) => {  

    return (dispatch) => {
        switch (mediaType) {
            case 'movie':
                dispatch({
                        type: IS_MOVIE_IMAGE_LOADED,
                        index: indexOf,
                        propId: id
                    })
                break;

            case 'tv':
                dispatch({
                        type: IS_TV_IMAGE_LOADED,
                        index: indexOf,
                        propId: id
                    })
                break;

            case 'person':
                dispatch({
                    type: IS_PERSON_IMAGE_LOADED,
                    index: indexOf,
                    propId: id
                })
                break;

            default:
                break;
        }
    };
};
