import { MEDIA_POPULAR, MOVIE_POPULAR, TV_POPULAR, PEOPLE_POPULAR,
    IS_MOVIE_IMAGE_LOADED, IS_TV_IMAGE_LOADED, IS_PERSON_IMAGE_LOADED,  ERROR } from '../Actions/types'

const INITIAL_STATE =  {
    media_popular:[
            {
            "id": '',
            "vote_count": '',
            "title": '',
            "release_date": '',
            "original_title": '',
            "backdrop_path": '',
            "poster_path": '',
            "media_type": '',
            "loading": true
            },
        ], 
    movie_popular:[
            {
                "id": '',
                "vote_count": '',
                "title": '',
                "release_date": '',
                "original_title": '',
                "backdrop_path": '',
                "poster_path": '',
                "media_type": '',
                "loading": true,
            },
        ],
    tv_popular:[
            {
                "id": '',
                "vote_count": '',
                "name": '',
                "release_date": '',
                "original_title": '',
                "backdrop_path": '',
                "poster_path": '',
                "media_type": '',
                "loading": true,
            },
        ],
    people_popular:[
            {
            "id": '',
            "profile_path": '',
            "name": '',
            "media_type": '',
            "loading": true
            },
        ],
    error: '',
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type){
        case MEDIA_POPULAR:
                //arrays to set variables
                const mediaArray = []
                const mediaLoadingArray = []
                //creat new media array
                for ( let i = 0; i < action.payload.results.length; i++) {
                    mediaArray.push(action.payload.results[i])
                  }
                  //create array for loading
                for ( let j = 0; j < action.payload.results.length; j++) {
                    const value = true;
                    mediaLoadingArray.push({"loading": value})
                }
                  //Join the two arrays by keys
                let mediaResults = mediaArray.map((e) => {
                    for(let element of mediaLoadingArray){
                        if(e.a === element.a) Object.assign(e, element);
                    }
                    return e;
                });   
            return{ ...state, media_popular: mediaResults }


        case MOVIE_POPULAR:
                //arrays to set variables
                const movieArray = []
                const movieLoadingArray = []
                //creat new media array
                for ( let i = 0; i < action.payload.results.length; i++) {
                    movieArray.push(action.payload.results[i])
                  }
                  //create array for loading
                for ( let j = 0; j < action.payload.results.length; j++) {
                    const value = false;
                    movieLoadingArray.push({"loading": value })
                }
                //Join the two arrays by keys
                let movieResult = movieArray.map((e1) => {
                    for(let element1 of movieLoadingArray){
                        if(e1.b === element1.b) Object.assign(e1, element1);
                    }
                    return e1;
                });
            return{ ...state, movie_popular: movieResult }


        case TV_POPULAR:
                //arrays to set variables
                const tvArray = []
                const tvLoadingArray = []
                //creat new media array
                for ( let i = 0; i < action.payload.results.length; i++) {
                    tvArray.push(action.payload.results[i])
                  }
                  //create array for loading
                for ( let j = 0; j < action.payload.results.length; j++) {
                    const value = true;
                    tvLoadingArray.push({"loading": value})
                }
                  //Join the two arrays by keys
                let tvResults = tvArray.map((e) => {
                    for(let element of tvLoadingArray){
                        if(e.a === element.a) Object.assign(e, element);
                    }
                    return e;
                });                
            return{ ...state, tv_popular: tvResults }


        case PEOPLE_POPULAR:
                //arrays to set variables
                const personArray = []
                const personLoadingArray = []
                //creat new media array
                for ( let i = 0; i < action.payload.results.length; i++) {
                    personArray.push(action.payload.results[i])
                  }
                  //create array for loading
                for ( let j = 0; j < action.payload.results.length; j++) {
                    const value = true;
                    personLoadingArray.push({"loading": value})
                }
                  //Join the two arrays by keys
                let personResults = personArray.map((e) => {
                    for(let element of personLoadingArray){
                        if(e.a === element.a) Object.assign(e, element);
                    }
                    return e;
                });   
                return{ ...state, people_popular: personResults }


        case IS_MOVIE_IMAGE_LOADED:
                const newMovieArray = []
                const movieImageLoadingArray = []
            //creat new media array
            for ( let i = 0; i < state.movie_popular.length; i++) {
                newMovieArray.push(state.movie_popular[i])
            }
            //create array for loading
            for ( let j = 0; j < state.movie_popular.length; j++) {
                const value = false;
                movieImageLoadingArray.push({"loading": value })
            }
             //Join the two arrays by keys
             let movieLoadingResults = newMovieArray.map((e) => {
                for(let element of movieImageLoadingArray){
                    if(e.a === element.a) Object.assign(e, element);
                }
                return e;
            });   

            return{ ...state, movie_popular: movieLoadingResults  }
        // case IS_TV_IMAGE_LOADED:
        //     return{ ...state, tv_popular:state.tv_popular[action.index].push(['loading'=action.payload])}
        // case IS_PERSON_IMAGE_LOADED:
        //     return{ ...state, people_popular:state.people_popular[action.index].push(['loading'=action.payload]) }
        case ERROR:
            return{ ...state, error: action.payload }
        default:
         return state;
    } 

};
