import { push } from 'connected-react-router'

  export const login = (username, password) => (dispatch) => {

    /* do something before redirection */

    dispatch(push('/home'))
  }

  export const pageNav = ( searchParam, mediaType ) => {
    return (dispatch) => {

        switch ( mediaType ) {

            case 'movie':
                dispatch(push({
                    pathname: `/movie`,
                    search: `Id=${searchParam}`,
                }))
                break;

            case 'tv':
                dispatch(push({
                    pathname: `/tv`,
                    search: `Id=${searchParam}`,
                }))
                break;

            case 'person':
                dispatch(push({
                    pathname: `/person`,
                    search: `Id=${searchParam}`,
                })) 

                break;
            default:
                break;
        }

    };
  };
