import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router'
import { getHomeInfo } from '../Actions';
import { Card, CardMedia, CardContent, Typography} from '@material-ui/core';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imageUrl: 'https://image.tmdb.org/t/p/w500',
            height: '',
            width: '',
            id: '',
            mediaType: '',
            // Arrays of cards to send to render
            Trending_movies: [],
            Trending_tv: [],
            Trending: [],
            Trending_people: [],
        };

        this.updateWindowDimensions = this.updateWindowDimensions.bind(this)
        this.pageNavigate = this.pageNavigate.bind(this)
    }



    componentDidMount() { 
        this.gatherAllInfo()
        window.addEventListener('resize', this.updateWindowDimensions);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }
    // Update window dimensions
    updateWindowDimensions() {
        this.setState({
            Height: window.innerHeight,
            Width: window.innerWidth
        });
    }

    gatherAllInfo(){
        this.props.getHomeInfo()
    }

    pageNavigate(value) {
        // Directs to correct page based upon selected item
        switch (value.value.props.mediatype) {
            case 'movie':
                this.props.history.push({
                    pathname: `/movie`,
                    search: `Id=${value.value.props.id}`,
                })
                break;
            case 'tv':
                this.props.history.push({
                    pathname: `/tv`,
                    search: `Id=${value.value.props.id}`,
                })
                break;
            case 'person':
                this.props.history.push({
                    pathname: `/person`,
                    search: `Id=${value.value.props.id}`,
                })

                break;
            default:
                break;
        }
    }

    createCards(coming, info, going){
        for (var i = 0; i < coming.length / 2; i++) {
            switch (info[i].media_type) {
                case 'movie':
                        this.setState(prevState => ({
                            going: [...prevState.myArray, <Card key={i} className='largeCard'>
                                                                <div className='CardMediaWrapper'>
                                                                    <CardMedia
                                                                        className='largeCardMedia'
                                                                        image={this.state.imageUrl + info[i].backdrop_path}
                                                                        title={info[i].title}
                                                                    />
                                                                    <CardContent className='CardOverlay'> 
                                                                    <i className="material-icons movieIcon ">movie</i>
                                                                    <div>
                                                                        <Typography variant="body1" className='overlayText'>
                                                                            {info[i].title}
                                                                        </Typography>
                                                                    </div>
                                                                    </CardContent>

                                                                </div>
                                                            </Card> ]
                          }))
                    break;
            };
        };
    }

    render() {
        console.log(this.state.mediaPopular)
        this.createCards('mediaPopular', this.props.mediaPopular, 'Trending')
        // this.createCards('moviePopular', this.props.moviePopular, 'Trending_movies')
        // this.createCards('tvPopular', this.props.tvPopular, 'Trending_tv')
        // this.createCards('peoplePopular', this.props.peoplePopular, 'Trending_people')
        // Setting page title
        document.title = 'Movie Database'
        // place to push objects after creation  
        let Trending_movies = []
        let Trending_tv = []
        let Trending = []
        let Trending_people = []
        // Laying out cards for popular 
        for (var i = 0; i < this.props.mediaPopular.length / 2; i++) {
            switch (this.props.mediaPopular[i].media_type) {
                case 'movie':
                        Trending.push(
                        <Card key={i} className='largeCard'>
                            <div className='CardMediaWrapper'>
                                <CardMedia
                                    className='largeCardMedia'
                                    image={this.state.imageUrl + this.props.mediaPopular[i].backdrop_path}
                                    title={this.props.mediaPopular[i].title}
                                />
                                <CardContent className='CardOverlay'> 
                                <i className="material-icons movieIcon ">movie</i>
                                <div>
                                    <Typography variant="body1" className='overlayText'>
                                        {this.props.mediaPopular[i].title}
                                    </Typography>
                                </div>
                                </CardContent>

                            </div>
                        </Card> 
                    );
                    break;
                case 'tv': 
                        Trending.push(
                            <Card key={i} className='largeCard'>
                                <div className='CardMediaWrapper'>
                                    <CardMedia
                                        className='largeCardMedia'
                                        image={this.state.imageUrl + this.props.mediaPopular[i].backdrop_path}
                                        title={this.props.mediaPopular[i].name}
                                    />
                                   <CardContent className='CardOverlay'> 
                                <i className="material-icons tvIcon ">tv</i>
                                <div>
                                    <Typography variant="body1" className='overlayText'>
                                        {this.props.tvPopular[i].name}
                                    </Typography>
                                </div>
                                </CardContent>
                                </div>
                            </Card> 
                        )
                    break;  
                default:
                    break;
            };
        }

 // laying out card for movie
    for (var k = 0; k < this.props.moviePopular.length; k++) {
        Trending_movies.push(
            <Card key={k} className='Card'>
            <div className='CardMediaWrapper'>
                <CardMedia
                    className='CardMedia'
                    image={this.state.imageUrl + this.props.moviePopular[k].poster_path}
                    title={this.props.moviePopular[k].title}
                />

            </div>
        </Card> 
    );
    }
 // laying out card for tv
 for (var l = 0; l < this.props.tvPopular.length; l++) {
    Trending_tv.push(
        <Card key={l} className='Card'>
        <div className='CardMediaWrapper'>
            <CardMedia
                className='CardMedia'
                image={this.state.imageUrl + this.props.tvPopular[l].poster_path}
                title={this.props.tvPopular[l].name}
            />

        </div>
    </Card> 
);
    }

        // laying out card for people
        for (var j = 0; j < this.props.peoplePopular.length; j++) {
                        Trending_people.push(
                        <Card key={j} className='Card'>
                            <div className='CardMediaWrapper'>
                                <CardMedia
                                    className='CardMedia'
                                    image={this.state.imageUrl + this.props.peoplePopular[j].profile_path}
                                    title={this.props.peoplePopular[j].name}
                                />
                                <CardContent className='CardOverlay'> 
                                <i className="material-icons personIcon ">person</i>
                                <div>
                                    <Typography variant="body1" className='overlayText'>
                                        {this.props.peoplePopular[j].name}
                                    </Typography>
                                </div>
                                </CardContent>
                            </div>
                        </Card> 
                    );
                }


        return (
            <div>
                <div className = 'slider'>
                    <div className = 'slideWrapper'> {Trending} </div>
                </div>
                <Typography variant="h5" className='overlayText'>
                    Movies
                </Typography>
                <div className = 'slider'>
                    <div className = 'slideWrapper'> {Trending_movies} </div>
                </div>
                <Typography variant="h5" className='overlayText'>
                    Television
                </Typography>
                <div className = 'slider'>
                    <div className = 'slideWrapper'> {Trending_tv} </div>
                </div>
                <Typography variant="h5" className='overlayText'>
                    People
                </Typography>
                <div className = 'slider'>
                    <div className = 'slideWrapper'> {Trending_people} </div>
                </div>
            </div>
        );
    }
}

// connections to Redux
const mapStateToProps = state => {
    const mediaPopular = state.home.media_popular
    const moviePopular = state.home.movie_popular
    const tvPopular = state.home.tv_popular
    const peoplePopular = state.home.people_popular

    return { mediaPopular, moviePopular, tvPopular, peoplePopular };
}

const mapDispatchToProps = (dispatch) => {
    return {
        getHomeInfo: (data) => dispatch(getHomeInfo(data)),
    };
};


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Home))
