export const actionTypes = {
    OPEN_SEARCHMODAL             : 'open_searchModal',
    CLOSE_SEARCHMODAL            : 'close_searchModal',
  }

  export function searchModalOpen( value ) {
    switch (value){
      case true:
          return {
            type: actionTypes.OPEN_SEARCHMODAL,
          }
          break;
      case false:
          return {
            type: actionTypes.CLOSE_SEARCHMODAL,
          }
          break;

      default:
          return { }    
    }
  }
