import {
    EMAIL_CHANGED,
    PASSWORD_CHANGED,
    LOGIN_USER_SUCCESS,
    LOGIN_USER_FAIL,
    LOGIN_USER,
    REGISTER_USER,
    REGISTER_USER_FAIL,
    REGISTER_USER_SUCCESS,
    REGISTER_EMAIL_ERROR,
    REGISTER_PASSWORD_ERROR
  } from '../Actions/types';

  const INITIAL_STATE = {
    email: '',
    password: '',
    user: '',
    error: '',
    loading: false,
    success: '',
    registerEmailError: '',
    registerPasswordError: '',
    registerError: ''
  };

  export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
      //Inputs changed
      case EMAIL_CHANGED:
        return { ...state, email: action.payload };
      case PASSWORD_CHANGED:
        return { ...state, password: action.payload };
      //Login Reducers
      case LOGIN_USER:
        return { ...state, loading: true, error: '' }; 
      case LOGIN_USER_SUCCESS:
        return { ...state, ...INITIAL_STATE, user: action.payload };
      case LOGIN_USER_FAIL:
        return { ...state, error: action.payload ,success:'', password: '', loading: false };
      //Register Reducers  
      case REGISTER_EMAIL_ERROR:
        return{ ...state, registerEmailError: action.payload};
      case REGISTER_PASSWORD_ERROR:
        return{ ...state, registerPasswordError: action.payload}
      case REGISTER_USER:
        return{ ...state, loading: true, error: ''};
      case REGISTER_USER_SUCCESS:
        return{ ...state, loading: false, password: '', user: action.payload, success: 'Successfully registered'};
      case REGISTER_USER_FAIL:
        return{ ...state, ...INITIAL_STATE, registerError:action.payload}; 
      default:
        return state;
    }
  };
