import {MEDIA_POPULAR, MOVIE_POPULAR, TV_POPULAR, PEOPLE_POPULAR, ERROR } from './types'

export const getHomeInfo = () => {  
    const url = 'https://api.themoviedb.org/3/trending/all/day?api_key=04ac5e20700da696a4b482b8e3d1c26e'

    return (dispatch) => {
        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: MEDIA_POPULAR,
                                payload: response
                            }))
        .then(dispatch(getPopularMovie( dispatch )))
        .then(dispatch(getPopularTv( dispatch )))
        .then(dispatch(getPopularPeople( dispatch )))
        .catch( (error) => { dispatch({ type:ERROR }) })
    };
};

const getPopularMovie = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/movie/popular?api_key=04ac5e20700da696a4b482b8e3d1c26e&language=en-US&page=1'

    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: MOVIE_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}

const getPopularTv = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/tv/popular?api_key=04ac5e20700da696a4b482b8e3d1c26e&language=en-US&page=1'

    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: TV_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}

const getPopularPeople = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/person/popular?api_key=04ac5e20700da696a4b482b8e3d1c26e&language=en-US&page=1'

    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: PEOPLE_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}
