import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router'
import { getHomeInfo } from '../Actions';
import { Card, CardMedia, CardContent, CardHeader, Typography} from '@material-ui/core';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            imageUrl: 'https://image.tmdb.org/t/p/w500',
            height: '',
            width: '',
            id: '',
            mediaType: '',
        };

        this.updateWindowDimensions = this.updateWindowDimensions.bind(this)
        this.pageNavigate = this.pageNavigate.bind(this)
    }



    componentDidMount() { 
        this.gatherAllInfo()
        window.addEventListener('resize', this.updateWindowDimensions);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }
    // Update window dimensions
    updateWindowDimensions() {
        this.setState({
            Height: window.innerHeight,
            Width: window.innerWidth
        });
    }

    gatherAllInfo(){
        this.props.getHomeInfo()
    }

    pageNavigate(value) {
        // Directs to correct page based upon selected item
        switch (value.value.props.mediatype) {
            case 'movie':
                this.props.history.push({
                    pathname: `/movie`,
                    search: `Id=${value.value.props.id}`,
                })
                break;
            case 'tv':
                this.props.history.push({
                    pathname: `/tv`,
                    search: `Id=${value.value.props.id}`,
                })
                break;
            case 'person':
                this.props.history.push({
                    pathname: `/person`,
                    search: `Id=${value.value.props.id}`,
                })

                break;
            default:
                break;
        }
    }

    render() {

        document.title = 'Movie Database'

        // Setting up how content will display
        let Trending_movies = []
        let Trending_tv = []
        let Trending = [ ...Trending_movies]
        console.log(Trending_movies)
        console.log(Trending_tv)
        console.log(Trending_movies.concat(Trending_tv))

        for (var i = 0; i < this.props.mediaPopular.length; i++) {
            switch (this.props.mediaPopular[i].media_type) {
                case 'movie':
                        Trending_movies.push(
                        <Card key={i} className='Card'>
                            <CardMedia
                                className='CardMedia'
                                image={this.state.imageUrl + this.props.mediaPopular[i].backdrop_path}
                                title={this.props.mediaPopular[i].title}
                            />
                            <div className='CardOverlay'> 
                                <Typography variant="body1" color="textPrimary">
                                    {this.props.mediaPopular[i].title}
                                </Typography>
                                <Typography variant="body2" color="textSecondary">
                                    {this.props.mediaPopular[i].release_date}
                                </Typography>
                            </div>
                        </Card> 
                    );
                    break;
                case 'tv': 
                        Trending_tv.push(
                            <Card key={i} className='Card'>
                                <CardMedia
                                    className='CardMedia'
                                    image={this.state.imageUrl + this.props.mediaPopular[i].backdrop_path}
                                    title={this.props.mediaPopular[i].name}
                                />
                                <CardHeader
                                title={this.props.mediaPopular[i].name}
                                subheader={this.props.mediaPopular[i].first_air_date}
                                /> 
                            </Card> 
                        )
                    break;
                default:
                    break;
            };
        }

        return (
            <div>
                {Trending}
                {Trending_movies}
                {Trending_tv}
            </div>
        );
    }
}

// connections to Redux
const mapStateToProps = state => {
    const mediaPopular= state.home.media_popular
    const peoplePopular= state.home.people_popular
    const query = state.search.query;
    const searched = state.search.searched;

    return { query, searched, mediaPopular, peoplePopular };
}

const mapDispatchToProps = (dispatch) => {
    return {
        getHomeInfo: (data) => dispatch(getHomeInfo(data)),
    };
};


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Home))
