import { MEDIA_POPULAR, MOVIE_POPULAR, TV_POPULAR, PEOPLE_POPULAR,
    IS_MOVIE_IMAGE_LOADED, IS_TV_IMAGE_LOADED, IS_PERSON_IMAGE_LOADED,  ERROR } from '../Actions/types'

const INITIAL_STATE =  {
    media_popular:[
            {
            "id": '',
            "vote_count": '',
            "title": '',
            "release_date": '',
            "original_title": '',
            "backdrop_path": '',
            "poster_path": '',
            "media_type": '',
            "loading": true
            },
        ], 
    movie_popular:[
            {
                "id": '',
                "vote_count": '',
                "title": '',
                "release_date": '',
                "original_title": '',
                "backdrop_path": '',
                "poster_path": '',
                "media_type": '',
                "loading": true,
            },
        ],
    tv_popular:[
            {
                "id": '',
                "vote_count": '',
                "name": '',
                "release_date": '',
                "original_title": '',
                "backdrop_path": '',
                "poster_path": '',
                "media_type": '',
                "loading": true,
            },
        ],
    people_popular:[
            {
            "id": '',
            "profile_path": '',
            "name": '',
            "media_type": '',
            "loading": true
            },
        ],
    homeError: '',
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type){

    //obtains image from fetch in action
        case MEDIA_POPULAR:
            return{ ...state, media_popular: action.payload.results }


        case MOVIE_POPULAR:
            return{ ...state, movie_popular: action.payload.results }


        case TV_POPULAR:        
            return{ ...state, tv_popular: action.payload.results }


        case PEOPLE_POPULAR:

                return{ ...state, people_popular: action.payload.results }

    //incase of any error retrieving fetch info in action
        case ERROR:
            return{ ...state, homeError: action.payload }
        default:
         return state;
    } 

};
