import {MEDIA_POPULAR, MOVIE_POPULAR, TV_POPULAR, PEOPLE_POPULAR, 
    IS_MOVIE_IMAGE_LOADED, IS_TV_IMAGE_LOADED, IS_PERSON_IMAGE_LOADED, ERROR } from './types'
// ADD ACTION TO CHANGE LOADING TO FALSE FOR IMAGES !!!!
export const ImageLoading = ( mediaType, Location, loaded ) => {  


    return (dispatch) => {
        switch (mediaType) {
            case 'movie':
                dispatch({
                        type: IS_MOVIE_IMAGE_LOADED,
                        index: Location,
                        payload: loaded
                    })
                break;

            case 'tv':
                dispatch({
                        type: IS_TV_IMAGE_LOADED,
                        index: Location,
                        payload: loaded
                    })
                break;

            case 'person':
                dispatch({
                    type: IS_PERSON_IMAGE_LOADED,
                    index: Location,
                    payload: loaded
                })
                break;

            default:
                break;
        }
    };
};

export const getHomeInfo = () => {  
    const url = 'https://api.themoviedb.org/3/trending/all/day?api_key=04ac5e20700da696a4b482b8e3d1c26e'

    return (dispatch) => {
        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: MEDIA_POPULAR,
                                payload: response
                            }))
        .then(dispatch(getPopularMovie( dispatch )))
        .then(dispatch(getPopularTv( dispatch )))
        .then(dispatch(getPopularPeople( dispatch )))
        .catch( (error) => { dispatch({ type:ERROR }) })
    };
};

const getPopularMovie = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/trending/movie/day?api_key=04ac5e20700da696a4b482b8e3d1c26e'


    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: MOVIE_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}

const getPopularTv = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/trending/tv/day?api_key=04ac5e20700da696a4b482b8e3d1c26e'


    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: TV_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}

const getPopularPeople = (dispatch) =>{
    const url = 'https://api.themoviedb.org/3/trending/person/day?api_key=04ac5e20700da696a4b482b8e3d1c26e'


    return ( dispatch ) => {

        fetch(url)
        .then((response) => response.json())
        .then(response => dispatch({
                                type: PEOPLE_POPULAR,
                                payload: response
                            }))
        .catch( (error) => { dispatch({ type:ERROR }) })
    }

}
