import { IS_MOVIE_IMAGE_LOADED, IS_TV_IMAGE_LOADED, IS_PERSON_IMAGE_LOADED } from './types'


export const ImageLoading = ( mediaType, Location ) => {  

    return (dispatch) => {
        switch (mediaType) {
            case 'movie':
                dispatch({
                        type: IS_MOVIE_IMAGE_LOADED,
                        index: Location,
                    })
                break;

            case 'tv':
                dispatch({
                        type: IS_TV_IMAGE_LOADED,
                        index: Location,
                    })
                break;

            case 'person':
                dispatch({
                    type: IS_PERSON_IMAGE_LOADED,
                    index: Location,
                })
                break;

            default:
                break;
        }
    };
};
