import { actionTypes }  from './action'

const initialState = {
  isOpen: false,
}

export default (state = initialState, action) => {
  switch(action.type) {

  case actionTypes.OPEN_SEARCHMODAL:
    return { isOpen: true }

  case actionTypes.CLOSE_SEARCHMODAL:
    return { isOpen: false }


}}
