import { actionTypes }  from './action'

const initialState = {
  isOpen: false,
  searchValue: '',
  target:null,
  searchBarResults:[
          {
          "id": '2',
          "vote_count": '',
          "title": '',
          "name": '',
          "release_date": '',
          "original_title": '',
          "backdrop_path": '',
          "poster_path": '',
          "media_type": '',
          },
      ], 
}

export default (state = initialState, action) => {
  switch(action.type) {

  case actionTypes.OPEN_SEARCHMODAL:
    return { ...state,  isOpen: true, searchValue: '', searchBarResults: state.searchBarResults }

  case actionTypes.CLOSE_SEARCHMODAL:
    return { ...state, isOpen: false }

  case actionTypes.UPDATE_SEARCH_TEXT:
      return { ...state, searchValue: action.payload, target: action.target }

  case actionTypes.SEARCH_BAR_RESULTS:
      return { ...state, searchBarResults: action.payload }

   default:
    return {state};
}}
